var getUrlParameter = function getUrlParameter(sParam) {
    var sPageURL = window.location.search.substring(1),
    sURLVariables = sPageURL.split('&'),
    sParameterName,
    i;

    for (i = 0; i < sURLVariables.length; i++) {
        sParameterName = sURLVariables[i].split('=');
        if (sParameterName[0] === sParam) {
            return sParameterName[1] === undefined ? true : decodeURIComponent(sParameterName[1]);
        }
    }
};

var client = getUrlParameter('client')
var special = getUrlParameter('special');
var referral = getUrlParameter('referral');
var modal = getUrlParameter('modal');

var urlSlug = '/' + client + '/' + special + '/';
var modalUrl = '/' + 'modals' + '/clients/' + client + '/' + modal + '/'
var fullModal = 'https://tools2.chumdev.com/' + modalUrl;
console.log(urlSlug);

$('.client').html(client);
$('.special').html(special);
$('.referral').html(referral);
$('.url').html(urlSlug);
$('.modal').html(modalUrl);

var woo = $("<div>").attr("id", "ca-" + special);
woo.iziModal({
    title: 'Here is your special for the' + ' ' + special,
    autoOpen: 1,
    onOpening: function (modal) {
        modal.startLoading();
        $.get(fullModal, function (data) {
            $("#ca-" + special + " .iziModal-content").html(data);
            modal.stopLoading();
            console.log("loading f150 ...")

        });
    }
});